import { View, Text, TextInput, TouchableOpacity, StyleSheet, Image} from 'react-native';

export default function LoginScreen() {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Login</Text>
      <View style={styles.logoContainer}>
  <Image source={require('../sa_2023/assets/texInClass')} style={styles.logo} />
</View>

      <View style={styles.inputContainer}>
        <TextInput
          placeholder="Email"
          style={styles.input}
        />
        {/* Emoji de email aqui */}
      </View>
      <View style={styles.inputContainer}>
        <TextInput
          placeholder="Senha"
          secureTextEntry
          style={styles.input}
        />
        {/* Emoji de cadeado aqui */}
      </View>
      <TouchableOpacity style={styles.loginButton}>
        <Text style={styles.buttonText}>Login</Text>
      </TouchableOpacity>
      <Text style={styles.signupText}>NÃ£o possui login? Cadastre-se</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    padding: 20,
    backgroundColor: '#fff',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  logoContainer: {
    // Estilos para o logotipo do site
    marginBottom: 20,
  },
  inputContainer: {
    flexDirection: 'row',
