import React from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import { Image } from 'react-native';

export default function LoginScreen() {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Login</Text>
      <Image source={require('../sa_2023/assets/textInClass.png')} style={styles.logo} />

      <View style={styles.inputContainer}>
        <Image source={require('../sa_2023/assets/cadeado.png')} style={styles.smallLogo} />
        <TextInput
          placeholder="Email"
          style={styles.input}
        />
      </View>
      <View style={styles.inputContainer}>
        <Image source={require('../sa_2023/assets/email.png')} style={styles.smallLogo} />
        <TextInput
          placeholder="Senha"
          placeholderStyle={styles.b}
          secureTextEntry
          style={styles.input}
        />
      </View>
      <TouchableOpacity style={styles.loginButton}>
        <Text style={styles.buttonText}>Login</Text>
      </TouchableOpacity>
      <Text style={styles.signupText}>Não possui login? Cadastre-se</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    padding: 20,
    backgroundColor: '#00AEA4', // Cor de fundo alterada
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    color: '#fff', // Cor do texto alterada
  },
  logoContainer: {
    marginBottom: 20,
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 25, // Bordas mais arredondadas
    paddingHorizontal: 15, // Espaçamento horizontal reduzido
    backgroundColor: '#fff', // Cor de fundo da barra
    elevation: 3, // Sombreamento
  },
  input: {
    flex: 1,
    paddingVertical: 8,
  },
  loginButton: {
    backgroundColor: '#007bff',
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 25, // Bordas mais arredondadas
    alignItems: 'center',
    marginTop: 10,
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
  },
  signupText: {
    marginTop: 20,
    color: '#fff', // Cor do texto alterada
  },
  logo: {
    width: 150,
    height: 150,
    resizeMode: 'contain',
  },
  smallLogo: {
    width: 24, // Tamanho reduzido
    height: 24, // Tamanho reduzido
    marginRight: 10,
  },
});
